// const navItems = links.map( link => NavLink(link) );

class Header extends React.Component {
  constructor(props) {
    super(props);
    this.state = { isOn: 'bit lore' }; 
  }

  changeHighlighted(title) {
    console.log('title : ' + title);
    this.setState( { isOn : title } );
    console.log('isOn : ' + this.state.isOn);
  }

  render() {
    return (
      <nav>
        <p>{this.state.isOn}</p>
        <NavLink title="bit lore" path="" viewing={this.changeHighlighted.bind(this)}/>
        <NavLink title="about" path="about" viewing={this.changeHighlighted.bind(this)} on={this.state.isOn}/>
        <NavLink title="work" path="work" viewing={this.changeHighlighted.bind(this)} on={this.state.isOn}/>
        <NavLink title="contact" path="contact" viewing={this.changeHighlighted.bind(this)} on={this.state.isOn}/>
        <style jsx>{`
          nav {
            display: flex;
            justify-content: space-between;
            margin: 0 auto;
            height: 2em;
            line-height: 2em;
          }

          @media only screen and (max-width:600px) {
            nav {
              text-align: center;
            }
          }
        `}</style>
      </nav>
    );
  }  
}

export default Header;
