// import Image from './img/bl_back.png';

// const img = require('./img/bl_back.png');
// import React from 'react';
// import style from 'next/css';

const mainStyle = style({ background: 'url(/static/img/bl_back.png) no-repeat center center scroll' })

const Backdrop = () => (
	<div className={mainStyle}>
		<style jsx>{`
			div {
				position: fixed;
				height: 100vh;
				width: 100vw;
				background-position: center 1em;
				background-repeat: no-repeat;
				background-size: auto 100vh;
				background-color: #202020;
				z-index: 0;
			}
		`}</style>
	</div>
);

export default Backdrop;
