// import ReactDOM from 'react-dom';
import Link from 'next/link';

class NavLink extends React.Component {

	constructor(props) {
		super(props);
	}

	render() {
		const view = (
			<div key={this.props.title} className={this.props.on ? 'on' : 'off'}>
				<Link href={`/${this.props.path}`}>
					<a>{this.props.title}</a>
				</Link>
				<style jsx>{`
					a {
						text-decoration: none;
						display: inline-block;
						width: 80%;
					}
					div {
						flex: 1;
						position: relative;
						padding: .5em 0 0 0;
						user-select: none;
					}
					div:not(.on) {
						cursor: pointer;
					}
					div::before {
						content: '';
						position: absolute;
						width: 0;
						height: 1.5em;
						border-bottom: 1px solid #444;
						transition: width .3s ease, border-color .3s;
					}
					div:hover::before {
						width: 80%;
					}
					div.on::before {
						width: 80%;
						border-bottom-color: whitesmoke;
					}

					@media only screen and (max-width:600px) {
						div:first-child {
							text-align: left;
						}
						div:last-child {
							text-align: right;
						}
					}
				`}</style>
			</div>
		);

		return view;
	}


}

// const navLink = new NavLink();

export default NavLink;



