// const navItems = links.map( link => NavLink(link) );

class Header extends React.Component {
  constructor(props) {
    super(props);
    this.state = { isOn: 'bit lore' }; 
  }

  handleClick(title) {
    this.setState( { isOn : title } );
  }

  render() {
    return (
      <nav>
        <NavLink title="bit lore" path="" on={this.state.isOn} onClick={this.handleClick()}/>
        <NavLink title="about" path="about" on={this.state.isOn} onClick={this.handleClick()}/>
        <NavLink title="work" path="work" on={this.state.isOn} onClick={this.handleClick()}/>
        <NavLink title="contact" path="contact" on={this.state.isOn} onClick={this.handleClick()}/>
        <style jsx>{`
          nav {
            display: flex;
            justify-content: space-between;
            margin: 0 auto;
            height: 2em;
            line-height: 2em;
          }

          @media only screen and (max-width:600px) {
            nav {
              text-align: center;
            }
          }
        `}</style>
      </nav>
    );
  }  
}

export default Header;
